DELIMITER $$
DROP TRIGGER if EXISTS `trigger_user_ari` $$
CREATE TRIGGER `trigger_user_ari`
    AFTER INSERT
    ON `users`
    FOR EACH ROW
BEGIN
    DECLARE avatar_male TEXT DEFAULT 'data:image/jpeg;base64,';
    DECLARE avatar_female TEXT DEFAULT 'data:image/jpeg;base64,';
    UPDATE users
        SET avatar = IF(NEW.gender = 'male', avatar_male, avatar_female)
    WHERE user_id = NEW.user_id;
END $$
